function diamondCut(
        FacetCut[] calldata _diamondCut,
        address _init,
        bytes calldata _calldata
    ) external override {
        LibDiamond.enforceIsContractOwner();
        LibDiamond.diamondCut(_diamondCut, _init, _calldata);
    }

function facets() external view override returns (Facet[] memory facets_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        uint256 numFacets = ds.facetAddresses.length;
        facets_ = new Facet[](numFacets);
        for (uint256 i; i < numFacets; i++) {
            address facetAddress_ = ds.facetAddresses[i];
            facets_[i].facetAddress = facetAddress_;
            facets_[i].functionSelectors = ds.facetFunctionSelectors[facetAddress_].functionSelectors;
        }
    }

function facetFunctionSelectors(address _facet) external view override returns (bytes4[] memory facetFunctionSelectors_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetFunctionSelectors_ = ds.facetFunctionSelectors[_facet].functionSelectors;
    }

function facetAddresses() external view override returns (address[] memory facetAddresses_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddresses_ = ds.facetAddresses;
    }

function facetAddress(bytes4 _functionSelector) external view override returns (address facetAddress_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddress_ = ds.selectorToFacetAndPosition[_functionSelector].facetAddress;
    }

function initializeERC20(string memory _name, string memory _symbol, uint8 _decimals) external {
        LibDiamond.enforceIsContractOwner();
        ERC20Storage storage es = erc20Storage();
        es.name = _name;
        es.symbol = _symbol;
        es.decimals = _decimals;
    }

function name() external view returns (string memory) {
        return erc20Storage().name;
    }

function symbol() external view returns (string memory) {
        return erc20Storage().symbol;
    }

function decimals() external view returns (uint8) {
        return erc20Storage().decimals;
    }

function totalSupply() external view returns (uint256) {
        return erc20Storage().totalSupply;
    }

function balanceOf(address account) external view returns (uint256) {
        return erc20Storage().balances[account];
    }

function transfer(address to, uint256 amount) external returns (bool) {
        _transfer(msg.sender, to, amount);
        return true;
    }

function allowance(address owner, address spender) external view returns (uint256) {
        return erc20Storage().allowances[owner][spender];
    }

function approve(address spender, uint256 amount) external returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

function transferFrom(address from, address to, uint256 amount) external returns (bool) {
        ERC20Storage storage es = erc20Storage();
        uint256 currentAllowance = es.allowances[from][msg.sender];
        require(currentAllowance >= amount, "ERC20: insufficient allowance");
        unchecked {
            _approve(from, msg.sender, currentAllowance - amount);
        }
        _transfer(from, to, amount);
        return true;
    }

function mint(address to, uint256 amount) external {
        LibDiamond.enforceIsContractOwner();
        ERC20Storage storage es = erc20Storage();
        es.totalSupply += amount;
        es.balances[to] += amount;
        emit Transfer(address(0), to, amount);
    }

function burn(uint256 amount) external {
        ERC20Storage storage es = erc20Storage();
        require(es.balances[msg.sender] >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            es.balances[msg.sender] -= amount;
        }
        es.totalSupply -= amount;
        emit Transfer(msg.sender, address(0), amount);
    }

