// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IDictionary.sol";

/// @title Dictionary - ERC-7546 Dictionary Implementation
/// @notice Manages the mapping of function selectors to implementation addresses
contract Dictionary is IDictionary {
    address private _owner;
    mapping(bytes4 => address) private implementations;

    modifier onlyOwner() {
        require(msg.sender == _owner, "Not owner");
        _;
    }

    constructor() {
        _owner = msg.sender;
    }

    function getImplementation(bytes4 selector) external view override returns (address) {
        return implementations[selector];
    }

    function setImplementation(bytes4 selector, address implementation) external override onlyOwner {
        implementations[selector] = implementation;
        emit ImplementationSet(selector, implementation);
    }

    function batchSetImplementations(bytes4[] calldata selectors, address[] calldata impls) external onlyOwner {
        require(selectors.length == impls.length, "Length mismatch");
        for (uint256 i = 0; i < selectors.length; i++) {
            implementations[selectors[i]] = impls[i];
            emit ImplementationSet(selectors[i], impls[i]);
        }
    }

    function owner() public view returns (address) {
        return _owner;
    }
}

