// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IDictionary.sol";

/// @title ModularProxy - ERC-7546 Modular Proxy
/// @notice A proxy that routes calls to implementations based on function selector
contract ModularProxy {
    address private _dictionary;

    constructor(address dictionary_) {
        _dictionary = dictionary_;
    }

    function dictionary() public view returns (address) {
        return _dictionary;
    }

    function _fallback() internal {
        address impl = IDictionary(_dictionary).getImplementation(msg.sig);
        require(impl != address(0), "Function not found");
        _delegate(impl);
    }

    function _delegate(address implementation) internal {
        assembly {
            calldatacopy(0, 0, calldatasize())
            let result := delegatecall(gas(), implementation, 0, calldatasize(), 0, 0)
            returndatacopy(0, 0, returndatasize())
            switch result
            case 0 { revert(0, returndatasize()) }
            default { return(0, returndatasize()) }
        }
    }

    fallback() external payable {
        _fallback();
    }

    receive() external payable {
        _fallback();
    }
}

