// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/// @title CounterFacet - Counter Implementation for ERC-7546
/// @notice Provides counter functionality as a modular implementation
contract CounterFacet {
    uint256 private _count;

    event CountIncremented(uint256 newCount);
    event CountDecremented(uint256 newCount);

    function count() external view returns (uint256) {
        return _count;
    }

    function increment() external {
        _count += 1;
        emit CountIncremented(_count);
    }

    function decrement() external {
        require(_count > 0, "Counter: cannot decrement below zero");
        _count -= 1;
        emit CountDecremented(_count);
    }
}

